-- ----------------------------
-- Table structure for blog_share
-- ----------------------------
DROP TABLE IF EXISTS `blog_share`;
CREATE TABLE `blog_share` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(50) NOT NULL COMMENT '书的标题',
  `logo` varchar(200) NOT NULL COMMENT '图片',
  `url` varchar(255) DEFAULT NULL COMMENT '图书详细地址URL',
  `produce_time` int(11) NOT NULL COMMENT '发布时间',
  `ready` int(11) DEFAULT '0' COMMENT '阅读量',
  `introduce` text COMMENT '描述',
  `status` int(11) NOT NULL DEFAULT '1' COMMENT '状态，默认为1',
  PRIMARY KEY (`id`),
  UNIQUE KEY `书名` (`title`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=27 DEFAULT CHARSET=utf8;

